import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [query, setQuery] = useState("nature");
  const [images, setImages] = useState([]);
  const getImages = async () => {
    await axios
      .get(`https://api.pexels.com/v1/search?query=${query}`, {
        headers: {
          Authorization: `jUeC6EnmP3bSLZVPFuLzitXzS0kH0aT46zTSrrxT4bSKcG41TDJ4mMMG`,
        },
      })
      .then((res) => setImages(res.data.photos));
  };

  const handleSearch = () => {
    getImages();
  };

  useEffect(() => {
    getImages();
  }, []);

  return (
    <div className="App">
      <div className="d-flex justify-content-center p-3">
        <input
          placeholder="Search for free photos"
          className="form-control search-bar me-2"
          onChange={(e) => setQuery(e.target.value ? e.target.value : "nature")}
          style={{ width: "20rem" }}
        />
        <button className="btn btn-success" onClick={() => handleSearch()}>
          Search
        </button>
      </div>
      <div className="row  m-0">
        {images.map((image, index) => (
          <div className="col-md-2 p-2">
            <img
              src={image.src.medium}
              style={{ width: "100%", height: "100%" }}
              className="rounded-2"
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
