import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [query, setQuery] = useState("nature");
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);

  const getImages = async () => {
    await axios
      .get(`https://api.pexels.com/v1/search?query=${query}`, {
        headers: {
          Authorization: `jUeC6EnmP3bSLZVPFuLzitXzS0kH0aT46zTSrrxT4bSKcG41TDJ4mMMG`,
        },
      })
      .then((res) => {
        setLoading(false);
        console.log(res.data);
        return setImages(res.data.photos);
      });
  };

  const handleSearch = () => {
    setLoading(true);
    getImages();
  };

  useEffect(() => {
    getImages();
  }, []);

  function download(url, name) {
    var link = document.createElement("a");
    link.href = url;
    link.download = image.alt;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

  return (
    <div className="App bg-light">
      <nav
        className="d-flex justify-content-between border-bottom p-3 mb-5 fixed-top"
        style={{ background: "white" }}
      >
        <img
          src="https://asset.brandfetch.io/idUIh6k_cy/idQgJhGfSi.png"
          style={{ height: "40px" }}
        />
        <span className="d-flex justify-content-center">
          <input
            placeholder="Search for free photos"
            className="form-control rounded-0 me-0"
            onChange={(e) =>
              setQuery(e.target.value ? e.target.value : "nature")
            }
            style={{ width: "20rem" }}
          />
          <button
            className="btn btn-success rounded-0"
            onClick={() => handleSearch()}
          >
            Search
          </button>
        </span>
      </nav>
      <div className="p-3 mt-5 pt-5">
        {loading ? (
          <div>
            <h3>Loading images...</h3>
            <div class="spinner-border text-warning" role="status">
              <span class="sr-only"></span>
            </div>
          </div>
        ) : (
          <div className="row  m-0">
            {images.map((image, index) => (
              <div className="container col-md-3 p-2">
                <img
                  src={image.src.medium}
                  style={{
                    width: "100%",
                    height: "100%",
                  }}
                  className="rounded-0"
                />
                <div className="overlay">
                  <p className="fs-4">{image.alt}</p>
                  <p>{image.photographer}</p>
                  <button
                    className="btn btn-light my-btn rounded-1 px-3"
                    onClick={() => download(image.src.medium, image.alt)}
                  >
                    Download <i class="fa fa-download"></i>
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
      <footer className="p-3 text-center">
        Copywright © Akash Sardar, 2023
      </footer>
    </div>
  );
}

export default App;
